import Component from "../baseComponent";
import Controler from "../modelAndControler";
let controler=new Controler();


class Knjiznicar extends Component {
    constructor() {
        super("div");

        this.dodajKnjigu = document.createElement("button");
        this.dodajKnjigu.innerHTML = "Dodaj Knjigu";
        this.dodajKnjigu.addEventListener("click", () => {
            this.show();
        });
        
        this.inputNaslov = document.createElement("input");
        this.inputNaslov.placeholder="Naslov Knjige";
        this.inputNaslov.style="display: none";

        this.sel = document.createElement("select");
        for(i=0; i<controler.model.autori.length; i++){
            let opt = document.createElement('option');
            opt.innerHTML = controler.model.autori[i].ime +" " +controler.model.autori[i].prezime;
            opt.value = controler.model.autori[i].ime +" " +controler.model.autori[i].prezime; 
            this.sel.appendChild(opt); 
        }
        this.sel.style="display: none";

        this.newAutorBtn = document.createElement("button");
        this.newAutorBtn.innerHTML = "Dodjate autora";
        this.newAutorBtn.addEventListener("click", () => {
            this.newAutor();
        });
        this.newAutorBtn.style="display: none";

        this.inputImeAutora = document.createElement("input");
        this.inputImeAutora.placeholder="Ime Autora";
        this.inputImeAutora.style="display: none";

        this.inputPrezimeAutora = document.createElement("input");
        this.inputPrezimeAutora.placeholder="Prezime Autora";
        this.inputPrezimeAutora.style="display: none";

        this.inputDatumAutora = document.createElement("input");
        this.inputDatumAutora.placeholder="Datum Rođenja Autora";
        this.inputDatumAutora.style="display: none";

        this.inputZanr = document.createElement("input");
        this.inputZanr.placeholder="Žanr";
        this.inputZanr.style="display: none";

        this.inputBrPrimjeraka = document.createElement("input");
        this.inputBrPrimjeraka.placeholder="Broj primjeraka";
        this.inputBrPrimjeraka.style="display: none";

        this.submitBtn = document.createElement("button");
        this.submitBtn.innerHTML = "SUBMIT";
        this.submitBtn.addEventListener("click", () => {
            this.onSubmit();
        });
        this.submitBtn.style="display: none";

        this.addChildren(
            [
                this.dodajKnjigu,
                this.inputNaslov,
                this.sel,
                this.newAutorBtn,
                this.inputImeAutora,
                this.inputPrezimeAutora,
                this.inputDatumAutora,
                this.inputZanr,
                this.inputBrPrimjeraka,
                this.submitBtn,
            ]
        )        
    }
    show(){
        this.inputNaslov.style="display: block";
        this.sel.style="display: block";
        this.newAutorBtn.style="display: block";
        this.inputZanr.style="display: block";
        this.inputBrPrimjeraka.style="display: block";
        this.submitBtn.style="display: block";
    }

    newAutor(){
        this.sel.style="display: none";
        this.inputImeAutora.style="display: block";
        this.inputPrezimeAutora.style="display: block";
        this.inputDatumAutora.style="display: block";
        this.newAutorBtn.style="display: none";
        alert("NEW AUTOR");
        controler.dispatchEvent(
            new CustomEvent(
                "newAutor",
                {detail: {autor: [this.inputImeAutora.value, this.inputPrezimeAutora, this.inputDatumAutora]}}
                )
        );

    }
    getInputData() {
        return {
            naslov: this.inputNaslov.value,
            autor: this.inputImeAutora.value +" " +this.inputPrezimeAutora.value,
            zanr: this.inputZanr.value,
            br: this.inputBrPrimjeraka.value
        }
    }
    onSubmit() {
        let inputData = this.getInputData();
        controler.addBook(
            inputData.naslov,
            inputData.autor,
            inputData.zanr,
            inputData.br,
        );

        for (i=this.sel.options.length-1; i >= 0; i--) {
            this.sel.options[i] = null;
        }
        for(i=0; i<controler.model.autori.length; i++){
            let opt = document.createElement('option');
            opt.innerHTML = controler.model.autori[i].ime +" " +controler.model.autori[i].prezime;
            opt.value = controler.model.autori[i].ime +" " +controler.model.autori[i].prezime; 
            this.sel.appendChild(opt); 
        }
    }
}

module.exports = Knjiznicar;
