import Component from "../baseComponent";
import Controler from "../modelAndControler";
let controler=new Controler();

class Prijava extends Component {
    constructor(){
        super("div");
        this.database = firebase.firestore();
        this.username=document.getElementById("username").
        this.password=document.getElementById("password").value
        
        document.getElementById("prijava").addEventListener("click", ()=>this.prijava())
        document.getElementById("registracija").addEventListener("click", ()=>this.registracija())
        
        
         
    }

    prijava(){
        let prijava=false //gleda da li se korisnik uspio prijaviti tj. da li je ispravno i username i password
        this.database.collection("korisnici").where("username", "==", this.username.value).get().then(function (querySnapshot) {
            querySnapshot.forEach(function (doc) {
              let podaci = doc.data();
              console.log(podaci)
              console.log(this.password.value)
              if(podaci.password==this.password.value){
                  alert("yey")
                  prijava=true //korisnik se prijavio
              }
            });
            prijava==false ? console.log("Netocno korisnicko ime ili lozinka") : false; //korisnik se nije prijavio
          });
        
    }
    registracija(){
        let vecZauzeto=false //provjerava je li username vez zauzet
        database.collection("korisnici").get().then((querySnapshot) => {
            querySnapshot.forEach((doc) => {
                doc.data().username==username.value? vecZauzeto=true : false;
            });
            if(vecZauzeto== true){
                console.log("Korisnicko ime koje ste upisali je već zauzeto")
            } else{
                database.collection("korisnici").add({
                    username: this.username.value,
                    password: this.password.value,
                    })
                console.log(`${this.username.value}, uspješno ste se registrirali `)
            }
            });
        }
    prijava2(event){
        console.log("RADI");
        let user = event.detail.user;

        if(user.status==="knjižničar"){
            let knjiznicar = new Knjiznicar();
            console.log(knjiznicar);
            let body = document.getElementsByTagName("body")[0];
            body.innerHTML="";
            body.appendChild(knjiznicar.rootElement); 
        }else if(user.status==="član"){
            console.log("dobro dosli clan")
        }
    }
    getInputData(){
        return [
            this.inputIme.value, 
            this.inputPrezime.value,
            this.inputOIB.value
        ]
    }
    
    onSubmit(){
        let inputData=this.getInputData();
        controler.getUser(
            inputData[0],
            inputData[1],
            inputData[2]
        );
        /*  */

        this.inputIme.value="";
        this.inputPrezime.value="";
        this.inputOIB.value="";

        
    }
}

module.exports = Prijava;
